app.post('/eventos', function (req, res) {

    let title = req.body.title
    let start = req.body.start
    let end = req.body.end
    try {
        const eventos = new Eventos({

            title: title,
            start: start,
            end: end,
            username: 'edwin'

        });
        eventos.save();
    } catch (err) {

        res.status(400).json({
            success: false,
            message: err.message
        })
    }

    res.status(200).json({
        success: true,
        message: "Evento ingresado con exito"
    })

})

